let display=document.querySelector(".display"),botoes=document.querySelectorAll(".btn"),clearButton=document.getElementById("limpar"),botaoIgual=document.getElementById("igual"),inputAtual="";function updateDisplay(){display.value=inputAtual}botoes.forEach(botao=>{botao.addEventListener("click",()=>{let value=botao.textContent;if("limpar"===botao.id){inputAtual=""}else{if("igual"===botao.id)try{let resultado=eval(inputAtual);if(!isFinite(resultado))throw new Error("Operação Inválida");inputAtual=parseFloat(resultado.toFixed(10)).toString()}catch(error){inputAtual="Erro"}else"Erro"===inputAtual&&(inputAtual=""),inputAtual+=value}updateDisplay()})});